query getEntityTypeInfo($type: String!) {
  management {
    getEntityTypeInfo(type: $type) {
      id
      icon
      displayName
      type
      route
      path
      active
      layoutConfiguration
      pageTemplateId
      externalFeatures {
        id
        displayName
        longDescription
        __typename
      }
      __typename
    }
    __typename
  }
}
